mapper.select:
SELECT 

FROM ad a

WHERE a.id = #{id}

SELECT 

FROM ad a


	and a.id = #{id}

SELECT 

FROM ad a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.ad_position_id = #{adPosition.id}


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.is_show = #{isShow}




		ORDER BY ${page.pageable.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ad a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.pageable.orderBy}


		ORDER BY a.update_date DESC

SELECT 	count(a.id) 
FROM ad a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

SELECT count(*)		 
FROM ad a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

mapper.update:
UPDATE ad SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE ad SET sort = CASE id
WHEN #{item.id} THEN #{item.sort}
END WHERE id in
#{item.id}
