mapper.select:
SELECT 

FROM company a

WHERE a.id = #{id}

SELECT
FROM company a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.short_name LIKE '%'||#{shortName}||'%' '%'+#{shortName}+'%' concat('%',#{shortName},'%')
AND is_pass = #{isPass}
AND home_show = #{homeShow}
ORDER BY ${page.pageable.orderBy}
ORDER BY a.update_date DESC

SELECT 
	* 
FROM company a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

SELECT count(id)
	* 
FROM company a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

mapper.update:
UPDATE company SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
