mapper.select:
SELECT 

FROM company_label a

WHERE a.id = #{id}

SELECT
FROM company_label a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.pageable.orderBy}
ORDER BY a.sort ASC

SELECT 
	* 
FROM company_label a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

SELECT count(id)
	* 
FROM company_label a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

mapper.update:
UPDATE company_label SET 	
	name = #{name},
	sort = #{sort},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE company_label SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
