mapper.select:
SELECT 

FROM industry a

WHERE a.id = #{id}

SELECT
FROM industry a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.parent_id = #{parent.id}
AND a.parent_ids LIKE '%'||#{parentIds}||'%' '%'+#{parentIds}+'%' concat('%',#{parentIds},'%')
ORDER BY ${page.pageable.orderBy}
ORDER BY a.sort ASC

SELECT 
	* 
FROM industry a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

SELECT count(id)
	* 
FROM industry a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM industry a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE industry SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE industry SET sort = CASE id
WHEN #{item.id} THEN #{item.sort}
END WHERE id in
#{item.id}
