mapper.select:
SELECT
FROM `order` a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.member_nickname LIKE '%'||#{memberNickname}||'%' '%'+#{memberNickname}+'%' concat('%',#{memberNickname},'%')
ORDER BY ${page.pageable.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM `order` a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.pageable.orderBy}


		ORDER BY a.update_date DESC

SELECT 	count(a.id) 
FROM `order` a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

SELECT count(*)		 
FROM `order` a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

mapper.update:
UPDATE `order` SET			
status = #{status},
update_date = #{updateDate}
WHERE id = #{id}
