mapper.select:
SELECT

FROM v_sys_user a

WHERE a.id = #{id}

SELECT

FROM v_sys_user a

WHERE a.username = #{username} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT
FROM v_sys_user a
JOIN sys_user_role ur ON ur.user_id = a.id AND ur.role_id = #{role.id}
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.id != '1'
AND a.username like '%'||#{username}||'%' CONCAT('%', #{username}, '%')
AND a.name like '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.pageable.orderBy}
ORDER BY a.name

SELECT 

FROM sys_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.pageable.orderBy}


		ORDER BY a.update_date DESC

SELECT 	count(a.id) 
FROM v_sys_user a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

SELECT count(*)		 
FROM v_sys_user a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}
