mapper.select:
SELECT 

FROM sys_sn a

WHERE a.id = #{id}

SELECT 

FROM sys_sn a



	and a.sn_type = #{snType}


	and a.id = #{id}

SELECT 

FROM sys_sn a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.last_value = #{lastValue}


		AND a.sn_type = #{snType}




		ORDER BY ${page.pageable.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_sn a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.pageable.orderBy}


		ORDER BY a.update_date DESC

SELECT 	count(*) 
FROM sys_sn a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

SELECT count(*)		 
FROM sys_sn a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

mapper.update:
UPDATE sys_sn SET 	
	last_value = #{lastValue},
	sn_type = #{snType},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE sys_sn SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
