SELECT 

FROM consultation_reply a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.parent_id = #{parent.id} 


		AND a.reply_type = #{replyType} 


		AND a.consultation_id = #{consultation.id} 




