SELECT
FROM coupon a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.pageable.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM coupon a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.pageable.orderBy}


		ORDER BY a.update_date DESC

SELECT 	count(*) 
FROM coupon a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

SELECT count(*)		 
FROM coupon a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.id = #{id}

mapper.update:
